export const registerMenu = (data, cb) => exports.ox_lib.registerMenu(data, cb);
export const showMenu = (id) => exports.ox_lib.showMenu(id);
export const hideMenu = (onExit) => exports.ox_lib.hideMenu(onExit);
export const getOpenMenu = () => exports.ox_lib.getOpenMenu();
export const setMenuOptions = (id, options, index) => exports.ox_lib.setMenuOptions(id, options, index);
