export const addRadialItem = (items) => exports.ox_lib.addRadialItem(items);
export const removeRadialItem = (item) => exports.ox_lib.removeRadialItem(item);
export const registerRadial = (radial) => exports.ox_lib.registerRadial(radial);
export const getCurrentRadialId = () => exports.ox_lib.getCurrentRadialId();
export const hideRadial = () => exports.ox_lib.hideRadial();
export const disableRadial = (state) => exports.ox_lib.disableRadial(state);
